package estruturaDeControles.lab;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String faixa= "marrom";
		
		switch (faixa) {
		case "preta":
			System.out.println("Seo o Bassai-dai");
			break;
		case "marrom":
			System.out.println("sei o Tekki shodan");
			break;
		default:
			break;
		}

	}

}
